import React, { PropTypes } from 'react'
import MuiCardText from 'material-ui/Card/CardText'

const CardText = ({ style, ...props }) =>
    <MuiCardText style={{
        paddingBottom: 0,
        ...style,
    }} {...props} />

CardText.propTypes = {
    style: PropTypes.object,
}

export default CardText
