import React, { PropTypes } from 'react'
import MuiCardTitle from 'material-ui/Card/CardTitle'

const CardTitle = ({ style, ...props }) =>
    <MuiCardTitle style={{
        paddingBottom: 0,
        ...style,
    }} {...props} />

CardTitle.propTypes = {
    style: PropTypes.object,
}

export default CardTitle
